function inetAton(ipv4Address) {
    // Split the IPv4 address into its four octets
    const octets = ipv4Address.split('.');

    // Validate that there are exactly four octets and they are valid numbers
    if (octets.length !== 4 || octets.some(octet => isNaN(octet) || octet < 0 || octet > 255)) {
        return null; // Return null for invalid IPv4 addresses, similar to SQL INET_ATON
    }

    // Convert each octet to an integer and combine them into a single 32-bit integer
    // The formula is (first_octet * 256^3) + (second_octet * 256^2) + (third_octet * 256^1) + (fourth_octet * 256^0)
    const ipInt = (parseInt(octets[0]) * Math.pow(256, 3)) +
        (parseInt(octets[1]) * Math.pow(256, 2)) +
        (parseInt(octets[2]) * Math.pow(256, 1)) +
        (parseInt(octets[3]) * Math.pow(256, 0));

    return ipInt;
}
msg.payload[0]["ipv4"] = inetAton(msg.payload[0]["ipv4"]);
return msg;

